import * as React from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import TextField from '@mui/material/TextField';
import { useAutocomplete } from '@mui/core/AutocompleteUnstyled';

const Listbox = styled('ul')(({ theme }) => ({
  width: 200,
  margin: 0,
  padding: 0,
  zIndex: 1,
  position: 'absolute',
  listStyle: 'none',
  backgroundColor: theme.palette.background.paper,
  overflow: 'auto',
  maxHeight: 200,
  border: '1px solid rgba(0,0,0,.25)',
  '& li[data-focus="true"]': {
    backgroundColor: '#4a8df6',
    color: 'white',
    cursor: 'pointer',
  },
  '& li:active': {
    backgroundColor: '#2977f5',
    color: 'white',
  },
}));


export default function BasicSelect() {
  const [location, setLocation] = React.useState('');
  const [purpose, setPurpose] = React.useState('');
  const [days, setDays] = React.useState('');
  const [vaccination, setVaccinations] = React.useState('');

  const handleChange = (event) => {
    setLocation(event.target.value);
    console.log(event.target);
    console.log(location);
    alert(1);
    // setAge(event.target.value);
  };

  const {
    getRootProps,
    getInputLabelProps,
    getInputProps,
    getListboxProps,
    getOptionProps,
    groupedOptions,
  } = useAutocomplete({
    id: 'use-autocomplete-demo',
    options: top100Films,
    getOptionLabel: (option) => option.title,
  });

  /**
  Name and Surname * (text)
  Location * (select)
  Purpose of WFO * (business, personal)
  How many days do you want to WFO * (1-5) 
  First day of WFO *(date)
  Preferable Room / Zone (text)
  I am fully vaccinated * (yes , no)
  Additional wishes (specific equipment, room for 1 person etc.) ) (text)
   */
  return (

    <Box>
      <FormControl sx={{ m:1, minWidth: 120 }}>
        <TextField name="name" required id="outlined-basic" label="Name and Surname" variant="outlined" />
      </FormControl>
      <FormControl sx={{ m:1, minWidth: 120 }}>
        <InputLabel id="demo-simple-select-label">Location</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          name="location"
          id="demo-simple-select"
          value={location}
          label="Location"
          onChange={handleChange}
        >
          <MenuItem value="kharkov">Kharkov</MenuItem>
          <MenuItem value="dnipro">Dnipro</MenuItem>
          <MenuItem value="kiev">Kiev</MenuItem>
        </Select>
      </FormControl>
      <FormControl sx={{ m:1, minWidth: 220 }}>
        <InputLabel required id="demo-simple-select-label">Purpose of WFO</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          name="purpose"
          id="demo-simple-select"
          value={purpose}
          label="Purpose"
          onChange={handleChange}
        >
          <MenuItem value="business">Business</MenuItem>
          <MenuItem value="personal">Personal</MenuItem>
        </Select>
      </FormControl>
      <FormControl sx={{ m:1, minWidth: 220 }}>
        <InputLabel required id="demo-simple-select-label">How many days do you want to WFO</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          name="days"
          id="demo-simple-select"
          value={days}
          label="Days"
          onChange={handleChange}
        >
          <MenuItem value="1">1</MenuItem>
          <MenuItem value="2">2</MenuItem>
          <MenuItem value="3">3</MenuItem>
          <MenuItem value="4">4</MenuItem>
          <MenuItem value="5">5</MenuItem>
        </Select>
      </FormControl>
      <FormControl sx={{ m:1, minWidth: 120 }}>
        <TextField name="date" required id="outlined-basic" label="Date" variant="outlined" />
      </FormControl>
      <FormControl sx={{ m:1, minWidth: 120 }}>
        <TextField name="room" required id="outlined-basic" label="Preferable Room / Zone" variant="outlined" />
      </FormControl>
      <FormControl sx={{ m:1, minWidth: 220 }}>
        <InputLabel required id="demo-simple-select-label">Vaccinated ?</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          name="vaccination"
          id="demo-simple-select"
          value={vaccination}
          label="Vaccinated ?"
          onChange={handleChange}
        >
          <MenuItem value="1">Yes</MenuItem>
          <MenuItem value="0">No</MenuItem>
        </Select>
      </FormControl>
      <FormControl sx={{ m:1, minWidth: 120 }}>
        <TextField name="info" required id="outlined-basic" label="Additional wishes (specific equipment, room for 1 person etc.)" variant="outlined" />
      </FormControl>
      <div {...getRootProps()}>
        <Label {...getInputLabelProps()}>useAutocomplete</Label>
        <Input {...getInputProps()} />
      </div>
      {groupedOptions.length > 0 ? (
        <Listbox {...getListboxProps()}>
          {groupedOptions.map((option, index) => (
            <li {...getOptionProps({ option, index })}>{option.title}</li>
          ))}
        </Listbox>
      ) : null}
    </Box>

  );
}
